import axios from 'axios'

const ORDEN_API_BASE_URL = 'http://localhost:8080/api/v1/ordenes'

const orden = {
  "fechaRecepcion": "2022-11-18",
  "cliente": {
      "razonSocial": "{{$randomProduct}}",
      "contacto": "{{$randomPhoneNumber}}"
  },
  "camion": {
      "cisternado": ["{{randomNumberCisternaA}}","{{randomNumberCisternaB}}","{{randomNumberCisternaC}}"],
      "descipcion": "{{$randomJobDescriptor}}",
      "patente": "{{$randomInt}}-{{$randomAbbreviation}}-{{$randomInt}}"
  },
  "chofer": {
      "apellido": "{{$randomLastName}}",
      "documento": "{{$randomInt}}",
      "nombre": "{{$randomFirstName}}"
  },
  "alerta": {
      "mail": "[ayecano98@gmail.com]",
      "temperaturaMax": 50
  },
  "producto": {
      "descipcion": "{{$randomProductAdjective}}",
      "nombre": "{{$randomProductName}}"
  },
  "codigoExterno": "{{$randomInt}}"
};

class OrdenService {
    async getOrdenes(token) {
      try {

        const response = await axios.get(ORDEN_API_BASE_URL, {
          headers: {
             'Authorization': `Bearer ${token}`
          }
        });

        return response.data;
      } catch (error) {
        throw error;
      }
    }

    async getOrdenesById(id, token) {
      try {

        const response = await axios.get(ORDEN_API_BASE_URL + '/' + id, {
          headers: {
             'Authorization': `Bearer ${token}`
          }
        });

        return response.data;
      } catch (error) {
        throw error;
      }
    }

    async createOrden(orden, token) {
      try {
        const response = await axios.post(ORDEN_API_BASE_URL, orden, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        return response.data;
      } catch (error) {
        throw error;
      }
    }


  }

  export default new OrdenService();
